css:
body{

    width : 730px;
}
/* CSS for styling the box */
        .container1 {
            display: flex; /* Use flexbox for layout */
            justify-content: left; /* Center items horizontally */
            /* Allow boxes to wrap to the next line */
            flex-wrap: wrap;
            width : 700px;

    }
        .box {
            width: 215px;
            height: 150px;

            border: 1px solid #333;
            border-radius: 10px;
            padding: 15px;
            box-sizing: border-box;

            margin: 5px; /* Adjust margin as needed */
        }
        p{
            padding: 1px; 
        }
@media print {
    body {
        margin: 10px; /* Set margins to 0.5 inches */
    }

    .box {
        padding: 10px; 
        height: 110px;/* Set padding to 0.25 inches */
    }

    p{
        padding: 1px; 

        }
}
